{title: 'welcome party', body: 'lorem ipsum ...', author: 'messi', id:2},
{title: 'web dev top', body: 'lorem ipsum ...', author: 'ronaldo', id:3},
]);

const handleDelete = (id) =>{
 const newBlogs = blogs.filter(blog => blog.id !== id );
 setBlogs(newBlogs);
}


  return (
    <div className="home">
    <BlogList blogs = {blogs} title = "All Blogs" handleDelete = { handleDelete} />
